import React from 'react';
import Card from './components/Card';
import Form from './components/Form';
import CardSaved from './components/CardSaved';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      cardName: '',
      cardDescription: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardImage: '',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
      cards: [],
    };

    this.ShowCards = this.ShowCards.bind(this);
    this.onInputChange = this.onInputChange.bind(this);
    this.onSaveButtonClick = this.onSaveButtonClick.bind(this);
  }

  onSaveButtonClick() {
    const {
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardName,
      cardDescription,
      cardImage,
      cardRare,
      cardTrunfo,
      cards,
    } = this.state;

    const objectCard = {
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardName,
      cardDescription,
      cardImage,
      cardRare,
      cardTrunfo,
    };
    const newCards = cards;
    newCards.push(objectCard);
    this.setState({ cards: newCards });
    if (cardTrunfo) {
      this.setState({ hasTrunfo: true });
    }
    const inputs = document.querySelectorAll('.input');
    for (let i = 0; i < inputs.length; i += 1) {
      if (inputs[i].type === 'number') {
        inputs[i].value = 0;
      } else {
        console.log("ent");
        inputs[i].value = '';
      }
    }
  }

  onInputChange({ target: { name, value, type, checked } }) {
    const nmb = 210;
    const maxnmb = 90;
    let atrValue = value;
    if (type === 'checkbox') {
      atrValue = checked;
    }
    this.setState({ [name]: atrValue }, () => {
      const {
        cardAttr1,
        cardAttr2,
        cardAttr3,
        cardName,
        cardDescription,
        cardImage,
      } = this.state;
      let attrs = [cardAttr1, cardAttr2, cardAttr3];
      const texts = [cardName, cardDescription, cardImage];
      attrs = attrs.map((x) => parseInt(x, 10));
      const v = parseInt(cardAttr1, 10)
      + parseInt(cardAttr2, 10) + parseInt(cardAttr3, 10);
      if (attrs.some((x) => x > maxnmb || x < 0)
      || texts.some((x) => x === '')
      || v > nmb) {
        this.setState({ isSaveButtonDisabled: true });
      } else {
        this.setState({ isSaveButtonDisabled: false });
      }
    });
  }

  ShowCards() {
    const { cards } = this.state;
    return (cards.map((x) => {
      const {
        cardAttr1,
        cardAttr2,
        cardAttr3,
        cardName,
        cardDescription,
        cardImage,
        cardRare,
        cardTrunfo,
        hasTrunfo,
      } = x;
      return (
        <CardSaved
          key={ cardName }
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
        />);
    }));
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
    } = this.state;
    return (
      <div>
        <h1>Tryunfo</h1>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          onSaveButtonClick={ this.onSaveButtonClick }
          onInputChange={ this.onInputChange }
          isSaveButtonDisabled={ isSaveButtonDisabled }
        />
        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
        />

        <h1>Suas cartas</h1>
        {this.ShowCards()}
      </div>
    );
  }
}

export default App;
