import React, { Component } from 'react';

class Settings extends Component {
  constructor() {
    super();
    this.state = {
      dificult: '',
      category: '',
      questions: '',
    };
    this.HandleInput = this.HandleInput.bind(this);
  }

  HandleInput({ target }) {
    const { value, name } = target;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <div className="login-container">
        <form>
          <div className="category-div">
            <button
              className="Category-button"
              type="button"
              name="category"
              value="22"
              onClick={ this.HandleInput }
            >
              geografy
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="21"
              onClick={ this.HandleInput }
            >
              Sports
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="11"
              onClick={ this.HandleInput }
            >
              Movies
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="10"
              onClick={ this.HandleInput }
            >
              Books
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default Settings;
