const INITIAL_STATE = '';

const token = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case 'REQUEST_TOKEN': return state;
  case 'GET_TOKEN': {
    const { token: codigo } = action;
    return codigo;
  }
  case 'FAIL_TO_GET_TOKEN': return state;
  default: return state;
  }
};

export default token;
