import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { SaveOptions } from '../actions/Index';

class Settings extends Component {
  constructor() {
    super();
    this.state = {
      difficulty: '',
      category: '',
      questions: '',
    };
    this.HandleInput = this.HandleInput.bind(this);
  }

  HandleInput({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });

    // const { storegeData } = this.props;
    // storegeData(target.value);
  }

  render() {
    return (
      <div className="login-container">
        <form>
          <div className="category-div">
            <h1>Category</h1>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="22"
              onClick={ this.HandleInput }
            >
              geografy
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="21"
              onClick={ this.HandleInput }
            >
              Sports
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="11"
              onClick={ this.HandleInput }
            >
              Movies
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="10"
              onClick={ this.HandleInput }
            >
              Books
            </button>
          </div>
          <div>
            <h1>DIFFICULTY</h1>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="easy"
              onClick={ this.HandleInput }
            >
              EASY
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="medium"
              onClick={ this.HandleInput }
            >
              MEDIUM
            </button>
            <button
              className="Category-button"
              type="button"
              name="category"
              value="hard"
              onClick={ this.HandleInput }
            >
              HARD
            </button>
          </div>
          <button type="button">
            BEGIN
          </button>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  storegeData: (data) => dispatch(SaveOptions(data)),
});

export default connect(null, mapDispatchToProps)(Settings);

Settings.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
  storegeData: PropTypes.func.isRequired,
};
