import React, { Component } from 'react';
import Header from '../components/Header';
import shuffle from '../services/shuffle';

class Trivia extends Component {
  constructor() {
    super();
    this.state = {
      perguntas: '',
      loading: true,
    };
    this.request = this.request.bind(this);
    this.renderPerguntas = this.renderPerguntas.bind(this);
    this.respondeu = this.respondeu.bind(this);
  }

  componentDidMount() {
    this.request();
  }

  respondeu({ target }) {
    console.log(target.innerHTML);
    //
  }

  request() {
    const token = localStorage.getItem('token');
    fetch(`https://opentdb.com/api.php?amount=5&token=${token}`)
      .then((r) => r.json()).then((perguntas) => {
        this.setState({ perguntas, loading: false });
      });
  }

  renderPerguntas() {
    const { perguntas } = this.state;
    return (
      perguntas.results.map((quest) => {
        const {
          category,
          dificulty,
          question,
        } = quest;
        const Alternativas = [quest.correct_answer, ...quest.incorrect_answers];
        const perguntasRand = shuffle(Alternativas);
        return (
          <div key={ dificulty }>
            <p>{ category }</p>
            <p>{ question }</p>
            <div>
              {perguntasRand.map((x, Index) => (
                <button
                  onClick={ this.respondeu }
                  type="button"
                  key={ Index }
                >
                  { x }
                </button>))}
            </div>
          </div>
        );
      })
    );
  }

  render() {
    const { loading } = this.state;
    return (
      <div>
        <Header />
        <div>{ loading ? null : this.renderPerguntas() }</div>
      </div>
    );
  }
}

export default Trivia;
