import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import md5 from 'crypto-js/md5';

class Feedback extends Component {
  constructor() {
    super();
    this.state = {
      img: '',
    };
    this.renderDados = this.renderDados.bind(this);
    this.renderFeedback = this.renderFeedback.bind(this);
  }

  componentDidMount() {
    this.renderDados();
  }

  renderDados() {
    const { email } = this.props;
    console.log(email);
    const hash = md5(email).toString();
    this.setState({ img: `https://www.gravatar.com/avatar/${hash}` });
  }

  renderFeedback() {
    const { score } = this.props;
    const media = 3;
    if (score < media) {
      return (<p>Could be better...</p>);
    }
    if (score === media) {
      return (<p>Well Done!</p>);
    }
    if (score > media) {
      return (<p>Well Done!</p>);
    }

  }

  render() {
    const { img } = this.state;
    const { name, score } = this.props;
    return (
      <div>
        <img data-testid="header-profile-picture" src={ img } alt="profile" />
        <h1 data-testid="header-player-name">{ name }</h1>
        <p data-testid="header-score">{ score }</p>
        <div>{ this.renderFeedback() }</div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  email: state.player.gravatarEmail,
  name: state.player.name,
  score: state.player.score,
});

export default connect(mapStateToProps)(Feedback);

Feedback.propTypes = {
  email: PropTypes.string.isRequired,
  name: PropTypes.string.isRequired,
  score: PropTypes.string.isRequired,
};
