import React, { Component } from 'react';
import Header from '../components/Header';

class Trivia extends Component {
  constructor() {
    super();
    this.state = {
      perguntas: '',
      loading: true,
    };
    this.request = this.request.bind(this);
    this.renderPerguntas = this.renderPerguntas.bind(this);
  }

  componentDidMount() {
    this.request();
  }

  request() {
    const token = localStorage.getItem('token');
    fetch(`https://opentdb.com/api.php?amount=5&token=${token}`)
      .then((r) => r.json()).then((perguntas) => {
        this.setState({ perguntas });
      });
  }

  renderPerguntas() {
    const { perguntas } = this.state;
    return (
      perguntas.results.map((quest) => {
        const {
          category,
          dificulty,
          question, correct = correct_answer, wrong = incorrect_answers } = quest;
        const questions = [correct, ...wrong];
        console.log(questions);
        return (
          <div key={ dificulty }>
            <p>{ category }</p>
            <p>{ question }</p>
            <div>{}</div>
          </div>
        );
      })
    );
  }

  render() {
    const { loading } = this.state;
    return (
      <div>
        <Header />
        <div>{ loading ? null : this.renderPerguntas() }</div>
      </div>
    );
  }
}

export default Trivia;
