const INITIAL_STATE = {
  player: {
    name: '',
    gravatarEmail: '',
  },
  assertions: '',
  score: '',
};

const player = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case 'ADD_EMAIL':
    return { ...state };
  case 'SAVE_DATA':
    console.log(action.dados);
    return { ...state, name: action.dados.name, gravatarEmail: action.dados.email };
  default:
    return { ...state };
  }
};

export default player;
