const INITIAL_STATE = {
  name: '',
  assertions: '',
  score: '',
  gravatarEmail: '',
};

const player = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case 'ADD_EMAIL':
    return { ...state };
  case 'SAVE_DATA':
    console.log(action);
    return { ...state, name: action.name, gravatarEmail: action.email };
  default:
    return { ...state };
  }
};

export default player;
