import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import shuffle from '../services/shuffle';
import { fetchApi } from '../actions/Index';
import Header from '../components/Header';
import './Trivia.css';

class Trivia extends Component {
  constructor() {
    super();
    this.state = {
      perguntas: '',
      loading: true,
      index: 0,
      isButtonDisabled: true,
      isRespDisabled: false,
      isColor: false,
    };
    this.request = this.request.bind(this);
    this.keepResp = this.keepResp.bind(this);
    this.secRequest = this.secRequest.bind(this);
    this.renderPerguntas = this.renderPerguntas.bind(this);
    this.respondeu = this.respondeu.bind(this);
  }

  componentDidMount() {
    this.request();
  }

  respondeu({ target }) {
    this.setState({ isRespDisabled: false, isButtonDisabled: true, isColor: false });
    const resposta = target.innerHTML;
    const { history } = this.props;
    const { perguntas, index } = this.state;
    const maxLength = 4;
    if (perguntas.results.some((x) => x.correct_answer === resposta)) {
      // AQUI É ONDE VC IMPLEMENTA O CODIGO DE RESPOSTA CERTA
      console.log('dd');
    }

    if (index < maxLength) {
      this.setState((prev) => this.setState({ index: prev.index + 1 }));
    } else {
      history.push('/feedback');
    }
  }

  request() {
    const token = localStorage.getItem('token');
    fetch(`https://opentdb.com/api.php?amount=5&token=${token}`)
      .then((r) => r.json()).then((perguntas) => {
        const invalidateToken = 3;
        if (perguntas.response_code === invalidateToken) {
          this.secRequest();
        } else {
          console.log(perguntas);
          this.setState({ perguntas, loading: false });
        }
      });
  }

  secRequest() {
    const { getToken } = this.props;
    getToken();
    const token = localStorage.getItem('token');
    fetch(`https://opentdb.com/api.php?amount=5&token=${token}`)
      .then((r) => r.json()).then((perguntas) => {
        const invalidateToken = 3;
        if (perguntas.response_code !== invalidateToken) {
          this.setState({ perguntas, loading: false });
        } else {
          this.secRequest();
        }
      });
  }

  keepResp() {
    this.setState({ isColor: true, isRespDisabled: true, isButtonDisabled: false });
  }

  renderPerguntas() {
    const { isColor, perguntas, index, isButtonDisabled, isRespDisabled } = this.state;
    const {
      category,
      dificulty,
      question,
    } = perguntas.results[index];
    const Alternativas = [perguntas.results[index].correct_answer,
      ...perguntas.results[index].incorrect_answers];
    const perguntasRand = shuffle(Alternativas);
    return (
      <div key={ dificulty }>
        <p data-testid="question-category">{ category }</p>
        <p data-testid="question-text">{ question }</p>
        <div data-testid="answer-options">
          {perguntasRand.map((x, Index) => {
            if (perguntas.results[index].correct_answer === x) {
              return (
                <button
                  className={ isColor ? 'activeC' : 'desactive' }
                  disabled={ isRespDisabled }
                  onClick={ this.keepResp }
                  type="button"
                  key={ Index }
                  data-testid="correct-answer"
                >
                  { x }
                </button>);
            }
            return (
              <button
                className={ isColor ? 'activeW' : 'desactive' }
                disabled={ isRespDisabled }
                onClick={ this.keepResp }
                type="button"
                key={ Index }
                data-testid={ `wrong-answer-${Index}` }
              >
                { x }
              </button>);
          })}
          <button
            disabled={ isButtonDisabled }
            onClick={ this.respondeu }
            type="button"
          >
            NEXT
          </button>
        </div>
      </div>
    );
  }

  render() {
    const { loading } = this.state;
    return (
      <div>
        <Header />
        <div>{ loading ? null : this.renderPerguntas() }</div>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  getToken: () => dispatch(fetchApi()),
});

Trivia.propTypes = {
  getToken: PropTypes.func.isRequired,
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};

export default connect(null, mapDispatchToProps)(Trivia);
